
public class Star {

	public static void main(String[] args) {
// TODO Auto-generated method stub
		int i=1,j=1;
		while(i<5)
		{
			while(j<5)
			{
				if(i+j>=5)
				{
				System.out.print("*");
				System.out.print(" ");
				
			}
		else
			{
			
				System.out.print(" ");
			}
		j++;
			}
			i++;
			j=1;
			
			System.out.println();
		
		
		}

	}


}